import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [page, setPage] = useState('');
  const [fontSize, setFontSize] = useState('');
  const [fontType, setFontType] = useState('');
  const [margin, setMargin] = useState('');
  const [response, setResponse] = useState('');
  const prompt = 'How many words would a ' + page + 'paper with font size ' + fontSize + 'and font style of ' + font

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post("/", { page })
      .then((res) => {
        setResponse(res.data)
      })
      .catch((err) =>{
        console.error(err)
      }
      )
  }
  return (
    <div className="App">
      <header className="App-header">
       How many words?
      </header>
      <div className = "Body">
        <h2> Fill out the following fields </h2>
        <form onSubmit={handleSubmit}>
          <label>
            How many pages is your essay?
            <input
            type="text"
            value = {page}
            onChange={(e) => setPage(e.target.value)}
           />


          </label>
          <br></br>
          <label>
            What is the font size of your essay?
            <input
            type="text"
            value = {fontSize}
            onChange={(e) => setFontSize(e.target.value)}
           />

          </label>
          <br></br>
          <label>
            What is the font type of your essay?
            <input
            type="text"
            value = {fontType}
            onChange={(e) => setFontType(e.target.value)}
           />

          </label>
          <br></br>
          <button type="submit">Submit</button>

        </form>

      </div>
      <div className = "Response">
        <p>Your word count is 10 words</p>


      </div>
    </div>


  );
}

export default App;
